package p43_substring;

public class p43_substring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="abate";
		String s2="bat";
		
		if(s1.contains(s2))
		{
			System.out.println("Substring");
		}
		else
		{
			System.out.println("Not a Substring");
		}

	}

}
